mapper.update:
UPDATE WAREHOUSE_HISTORY 
SET amount = #{amount}, warehouse_code = #{warehouse_code}, emp_code = #{emp_code}
WHERE io = 'IN' AND code = #{code}

UPDATE ORDERS
SET complete = 1
WHERE code = #{code}

UPDATE ORDERS 
SET amount = #{amount}, material = #{material}
WHERE code = #{code} AND status = '신청'

mapper.select:
SELECT code, name FROM MAPD WHERE group_id = 'MAT'
AND name LIKE CONCAT('%', #{searchName}, '%')
LIMIT #{cri.startPage}, #{cri.pageSize}
