mapper.select:
SELECT r.*, c.name clientName, m.name productName
FROM REQUEST r
LEFT JOIN CLIENT c on r.client_code = c.code
RIGHT join STOCK s on r.product = s.mapd_code
LEFT join MAPD m on r.product = m.code
where 1 = 1
 AND r.client_code = #{client_code} 
 AND m.name = #{productName} 



	AND r.date between #{startDate} and #{endDate} 


	AND r.date = #{endDate} 
	AND r.date = #{startDate} 





	AND r.deadline between #{startDead} and #{endDead} 


	AND r.deadline = #{endDead} 

	AND r.deadline = #{startDead} 


 AND r.manager = #{manager} 

 AND r.status in 

 #{status} 


order by r.reg_date DESC

mapper.update:
update REQUEST set client_code=#{vo.client_code}, deadline=#{vo.deadline}, date=#{vo.date}, 
manager=#{vo.manager}, product=#{vo.product}, amount=#{vo.amount}, unit=#{vo.unit}, 
stock=#{vo.stock}, currency=#{vo.currency}, status=#{vo.status}, 
update_date=now(), update_emp=#{userid}
where code = #{vo.code}

mapper.delete:
delete from REQUEST
where code in 

 #{codeNum}
